import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Redirect } from 'react-router-dom';

import Inicio from './Inicio';

export function Login() {
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const [messages, setMessages] = useState()
    const [redirectToComponent, setRedirectToComponent] = useState(false);


    const API = 'http://localhost:5000/api/usuario'
    // para mostrar los datos actuales
    // useEffect(() => {
    //     axios.get('http://localhost:5000/api/usuario/obtenerUsuarios') 
    //     .then(res => {
    //         const user = res.data
    //         const cada_user = user.map(usuarioInd )
    //       console.log(user)

    //     }).catch(err => {
    //       console.log(err)
    //     })
    // })

    // Manejar el submit
    const handleSubmit = (e) => {
        e.preventDefault();
        const user_To_Login = {
            email: email,
            password: password
        }
        // hacer la peticion al backend
        axios.post(`${API}/login`, user_To_Login)
            .then(res => {
                if (res.data === false) {
                    alert('No mio')
                    setMessages('Error, Email or password wrong')
                }
                else {
                    console.log(res.data);
                    alert('Si mio');
                    setMessages('Logeado correctamente!!');
                    setRedirectToComponent(true);
                }
                if (redirectToComponent) {
                    return <Redirect to="/Inicio" />;
                }

            })
            .catch(error => {
                console.log('Error en la petición:', error);
            });
    }

    return (
        <div className="row" >
            <h3>{messages}</h3>
            <div className="col-md-6">
                <form onSubmit={handleSubmit} className="card card-body">
                    {/* para el emails */}
                    <div className="form-group">
                        <input type="email"
                            onChange={e => setEmail(e.target.value)}
                            value={email}
                            className="form-control"
                            placeholder="Email"
                        />
                    </div>
                    <br></br>
                    {/* para la contrase;a */}
                    <div className="form-group">
                        <input type="password"
                            onChange={e => setPassword(e.target.value)}
                            value={password}
                            className="form-control"
                            placeholder="Password"
                        />
                    </div>
                    <br></br>

                    <button className="btn btn-primary btn-block">
                        Login
                    </button>
                </form>
            </div>
            {/* SHOW THE USERS WE GOT IN THE DB  */}

        </div >
    )
}
