    //         //     setTasks(tasks.filter(task => task._id === id))

    //         // }

    //     } catch (error) {
    //         console.log(error)
    //     }
    // }

    return (
        // <TaskContent.Provider value={{ tasks, createTask, getTasks }}>
        <TaskContent.Provider value={{ tasks, createTask, getTasks, isAuthenticated, deleteTask }}>
            {children}
        </TaskContent.Provider>
    )
}
