import React from 'react'
import { useAuth } from '../context/authContext'
import { useTasks } from '../context/TaskContext';
// import VerNotas from '../Components/VerNotas';

export default function VerNotas() {
    const { getTasks, tasks } = useTasks();

    if (!tasks) return (<h5>No hay Tareas</h5>)

    return (
        tasks.map(task => (
            // <div key={task._id}>
            //   <h6>{task.titulo}</h6>
            //   <p>{task.descripcion}</p>
            // </div>
            //desde aqui empieza la parte tomada de github
            <div className="container col-md-6" key={task._id}>
                <div className="row">
                    <div>
                        <ul className="list-group">
                            {/* <li className="list-group-item" >{task._id}</li> */}
                            <li className="list-group-item"><h3>Titulo: </h3>{task.titulo}</li>
                            <li className="list-group-item"><h3>Descripcion: </h3>{task.descripcion}</li>
                            <li className="list-group-item"><h3>Fecha: </h3>: {task.fecha_creada}</li>
                        </ul>
                        {/* <Link to={`/editartask/${task.idtask}`}><ul className='btn btn-success'>Editar</ul></Link> */}
                        {/* &nbsp; */}
                        <button className='btn btn-danger'> Eliminar</button>
                        &nbsp;

                        <button className='btn btn-outline-primary'> Editar</button>
                        <hr className="mt-4" />
                    </div>
                </div>
            </div>

        ))
    )
}
