import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Login } from './Login';

export default function Inicio({ userdata }) {
  // Hooks
  const [loggedIn, setLoggedIn] = useState(true); // establecer en true inicialmente
  const [titulo, setTitulo] = useState();
  const [descripcion, setDescripcion] = useState();
  const [messages, setMessages] = useState();

  // Mostrar los datos actuales
  useEffect(() => {
    if (!loggedIn) {
      return; // No realizar la lógica si no está loggedIn
    }

    axios.get(`http://localhost:5000/api/usuario/nota/${userdata._id}`)
      .then(res => {
        console.log(res.data);
      })
      .catch(err => {
        console.log(err);
      });
  }, [loggedIn, userdata]);

  // Manejo del Submit
  const handleSubmit = () => {
    // Lógica para el envío del formulario
  };

  if (!loggedIn) {
    return <Login />;
  }

  return (
    <div className="row">
      <div className="row">Bienvenida {userdata.nombre}</div>
      <h3 style={{ color: "red" }}>{messages}</h3>
      <div className="col-md-4">
        <form onSubmit={handleSubmit} className="card card-body">
          {/* para el Titulo */}
          <div className="form-group">
            <input
              type="text"
              onChange={e => setTitulo(e.target.value)}
              value={titulo}
              className="form-control"
              placeholder="Titulo"
              required
            />
          </div>
          <br></br>
          {/* para la Descripcion */}
          <div className="form-group">
            <input
              type="text"
              onChange={e => setDescripcion(e.target.value)}
              value={descripcion}
              className="form-control"
              placeholder="Descipcion"
              required
            />
          </div>
          <br></br>

          <button className="btn btn-primary btn-block">Guardar</button>
        </form>
      </div>
      {/* SHOW THE NOTES WE GOT IN DB*/}
    </div>
  );
}


